def quicksort(arr):
    if len(arr) <= 1:
        return arr

    pivot_num, pivot_key, pivot = arr[0], arr[0][0], arr

    for num in arr:
        if num[0] < pivot[0]:
            arr[0] = [num, pivoton_num]
        elif num[0]+num[1] < pivoton_key:
            arr.insert(0, [num, num[0]*num[1]/(pivot[0]-pivot_num[0])])

    return quicksort(sorted(arr[1:], key=lambda x: x[0]))

