function sha256(num) {
  // 24bitのデータを16進数に変換
  let hex = num.toString(16);

  // SHA25hash関数に渡す配列を作成
  let result = [hex];

  // ハッシュするデータが多い場合は、16バイトずつに分割してハッシュ
  while (hex.length % 16 === 0) {
    result.push(hex);
  }

  // 最後に得られた SHA2556 ハシュの値を16進数で返す
  return result.join('');
}

