int is_valid_mac_address(const char *mac_address) {
    if (strlen(mac_address) != 17) {
        return 0;
    }

    const char *delimiters = ":.-";
    char addresses[2][18];
    int address_count = 0;

    char *token = strtok(mac_address, delimiters);

    while (token && address_count < 2) {
        strncpy(addresses[address_count], token, sizeof(addresses[address_count]));
        address_count++;
        token = strtok(NULL, delimiters);
    }

    if (address_count != 2) {
        return 0;
    }

    int i, j;
    for (i = 0, j = 1; j < 17; i++, j += 3) {
        if (!isxdigit(addresses[0][i]) || !isxdigit(addresses[1][j])) {
            return 0;
        }

