```go
package main

import (
	"fmt"
	"strings"
)

// Producer 生産者のゴルーチン
func Producer(ch chan<- string) {
	defer wg.Done()
	var input string
	fmt.Scanln(&input)
	ch <- input
}

// Consumer 消費者のゴルーチン
func Consumer(ch <-chan string, wg *sync.WaitGroup) {
	defer wg.Done()
	input := <-ch
	processed := strings.ToUpper(input)
	fmt.Println(processed)
}

func main() {
	ch := make(chan string)
	wg := &sync.WaitGroup{}

	// 2つのゴルーチンを開始
	wg.Add(2)
	go Producer(ch)
	go Consumer(ch, wg)

	// ゴルーチンの終了を待つ
	wg.Wait()
}
```

