```javascript
function findObjectByName(obj, name) {
  let found = null;
  if (Array.isArray(obj)) {
    for (const element of obj) {
      found = findObjectByName(element, name);
      if (found) break;
    }
  } else if (obj instanceof Object) {
    for (const key in obj) {
      if (key === name) {
        found = obj[key];
        break;
      }
      found = findObjectByName(obj[key], name);
      if (found) break;
    }
  }

  // パスをオブジェクトの文字列表現と結合する
  if (found) {
    return `(${JSON.stringify(obj)}).${name}`;
  } else {
    return null;
  }
}
```

