	const (
		totalStrings = 1000000 // 単語の総数
		wordLength  = 6       // 単語の長さ
		randomFactor = 20      // ランダム性を増すための係数
	)

	// 文字列を小文字に変換
	s = strings.ToLower(s)

	// 頻出する単語のリスト
	commonWords := []string{"the", "be", "to", "of", "and", "a", "in", "that", "have", "it"}

	// 文字列に含まれる単語が頻出単語か判定
	isCommon := false
	for _, word := range commonWords {
		if strings.Contains(s, word) {
			isCommon = true
			break
		}
	}

	// 頻出単語を含む場合は美しさ確率を低くする
	if isCommon {
		return 1.0 / float64(totalStrings)
	}

	// 文字列に含まれる文字の出現頻度をカウント
	charCount := make(map[rune]int)
	for _, r := range s {
		charCount[r]++
	}

	// 文字の出現頻度を基に美しさ確率を計算
	sum := 1.0
	for _, count := range charCount {
		sum *= float64(randomFactor*wordLength+totalStrings) / float64(count+totalStrings)
	}

	return sum / float64(len(s))
}
