```go
package main

import (
	"context"
	"fmt"
	"os"

	corev1 "k8s.io/api/core/v1"
	metav1 "k8s.io/apimachinery/pkg/apis/meta/v1"
	"k8s.io/client-go/kubernetes"
	"k8s.io/client-go/rest"
	"k8s.io/client-go/tools/clientcmd"
)

func main() {
	 // Load the kubeconfig file and initialize a client.
	 kubeconfig := clientcmd.NewNonInteractiveDeferredLoadingClientConfig(
		 clientcmd.NewDefaultClientConfigLoadingRule(false),
		 &clientcmd.ConfigOverrides{},
	 )
	 config, err := kubeconfig.ClientConfig()
	 if err != nil {
		 panic(err.Error())
	 }
	 config.Timeout = 30 * time.Second
	 // creates the clientset
	 clientset, err := kubernetes.NewForConfig(config)
	 if err != nil {
		 panic(err.Error())
	 }

	 // Create a namespace
	 name := "my-new-namespace"
	 newNamespace := &corev1.Namespace{
		 ObjectMeta: metav1.ObjectMeta{
			 Name: name,
		},
	 }
	 // Use the CoreV1Client to create the namespace
	 createNamespaces, err := clientset.CoreV1().Namespaces().Create(context.TODO(), newNamespace, metav1.CreateOptions{})
	 if err != nil {
		 fmt.Println(err.Error())
	 }
	 fmt.Println("Created Namespace: " + createNamespaces.Name)

}
```

