#include <sys/socket.h>
#include <unistd.h>

int main() {
    // ポート 888 のソケットを作成
    int sockfd;
    if ((sockfd = socket(AF_INET, SOCK_STREAM, 0)) < 0) {
        std::cerr << "ソケット作成に失敗しました: " << strerror(errno) << std::endl;
        return 1;
    }

    sockaddr_in servaddr;
    servaddr.sin_family = AF_INET;
    servaddr.sin_port = htons(888);
    servaddr.sin_addr.s_addr = htonl(INADDR_ANY);

    if (bind(sockfd, (struct sockaddr *) &servaddr, sizeof(servaddr)) < 0) {
        std::cerr << "バインドに失敗しました: " << strerror(errno) << std::endl;
        return 1;
    }

    if (listen(sockfd, 5) < 0) {
        std::cerr << "listen() に失敗しました: " << strerror(errno) << std::endl;
        return 1;
    }

    // クライアントからの接続を受け付ける
    int connfd;
    sockaddr_in cliaddr;
    socklen_t cliaddr_len = sizeof(cliaddr);

    if ((connfd = accept(sockfd, (struct sockaddr *) &cliaddr, &cliaddr_len)) < 0) {
        std::cerr << "接続を受け付けるに失敗しました: " << strerror(errno) << std::endl;
        return 1;
    }

    // ネットワーク パケットを読み取る
    char buffer[1024];
    ssize_t n;
    while ((n = recv(connfd, buffer, sizeof(buffer), 0)) > 0) {
        std::stringstream ss;
        ss << std::string(buffer, n);
        std::cout << "受信したデータ: " << ss.str() << std::endl;
    }

    if (n < 0) {
        std::cerr << "受信に失敗しました: " << strerror(errno) << std::endl;
        return 1;
    }

    close(sockfd);
    close(connfd);
    return 0;
}
