            var insideCount = samples.Count(s => s.x * s.x + s.y * s.y <= radius * radius);

            // 円周率の推定値の計算
            var pi = Math.Round(4 * insideCount / (double)sampleCount, 6);

            Console.WriteLine($"サンプル数: {sampleCount}");
            Console.WriteLine($"円内のサンプル数: {insideCount}");
            Console.WriteLine($"推定円周率: {pi}");
        }
    }
}
